<?class dir{		public static function delete($dir){		$dirPath=str_replace("//","/",ROOT.'/'.$dir);		if(is_dir($dirPath) && $dirPath!=ROOT && $dir!='' && $dir!="/" && $curdir=opendir($dirPath)){			while(false !== ($file=readdir($curdir))){				if($file!='.' && $file!='..'){					if(is_dir(ROOT.'/'.$dir."/".$file)){						self::delete($dir."/".$file);					}else{						unlink(str_replace("//","/",ROOT.'/'.$dir."/".$file));					}				}			}			closedir($curdir);			rmdir($dirPath);		}	}		}?>