<?class msg{	private static $in=array();	private static $out=array();	private static $cookieName='messager';	private static $initStatus=0;	private function __construct(){	}	private function __clone(){	}	public function set($name,$value,$flag=true){		if($flag===true){			self::$in[$name][]=$value;			setcookie (self::$cookieName,serialize(self::$in),time()+120, "/");		}else{			self::$out[$name][]=$value;		}	}	public function get($name){		if(isset(self::$out[$name])){			$str=array();			foreach(self::$out[$name] as $value){				$str[]=$value;			}			$str=implode("<br />",$str);		}		return $str;	}	public function init(){		if(self::$initStatus===0){			$data=$_COOKIE[self::$cookieName];			setcookie (self::$cookieName,"",time()-3600, "/");			if(isset($data)){				$data=unserialize($data);				if(is_array($data))self::$out=$data;			}			self::$initStatus=1;		}	}	public function import($data,$flag=true){		if($flag===true){			foreach($data as $name=>$value){				self::$in[$name][]=$value;			}			setcookie (self::$cookieName,serialize(self::$in),time()+120, "/");		}else{			foreach($data as $name=>$value){				self::$out[$name][]=$value;			}		}	}}?>