<?class str{	public function crop($str,$int,$flag=NULL){		if(mb_strlen($str)>$int){			$str=mb_substr($str,0,$int);			$tmp=mb_strrpos($str," ");			if(!$tmp)$tmp=$int;			$str=mb_substr($str,0,$tmp);									if($str && !preg_match("/[a-zA-Zа-яА-Я]+/i",mb_substr($str, -1))){				$str=mb_substr($str,0,(mb_strlen($str)-1));			}						if($flag){				$str.=$flag;			}			}		return $str;	}}?>