<table border=0 cellpadding='5' ><?if($update){?><form method='post'>	<tr>		<td>Категория: </td>	<td>	<select name='id'>		<?foreach($list as $cat){		?>		<option <?if($id && $id==$cat['id'])echo 'selected="selected"';?>value=<?=$cat['id']?>><?=$cat['name']?></option>		<?		}?>	</select>	<input type='submit' value='Выбрать' />	</td>	</tr></form><?}?><?if(!$update || ($update && $id)){?><form method='post' action='/action/genre/<?if($update){?>change<?}else{?>add<?}?>/'  enctype='multipart/form-data'>	<?	if($id){	?>		<input type='hidden' name='id' value='<?=$id?>' />	<?	}	?>	<tr>		<td>Название:</td>		<td><input type='text' name='name' value='<?=$name?>' /></td>	</tr>	<tr>		<td>Изображение:</td>		<td><input type='file' name='img' /></td>	</tr>	<tr>		<td></td>		<td>			<?if($img){?>				<img src='<?=$img?>' />			<?}?>		</td>	</tr>	<tr>		<td>Текст:</td>		<td><textarea name='text' style='width:550px;height:300px'><?=$text?></textarea></td>	</tr>			<tr>		<td>Аниме<br />(для жанров аниме):</td>		<td><input type='checkbox' name='anime'  value='1' <?if($anime) echo 'checked="checked"';?> /></td>	</tr>		<tr>		<td></td>		<td><?=$captcha->img?></td>	</tr>			<tr>		<td>Введите код</td>		<td><?=$captcha->form?></td>	</tr>	<tr>		<td></td>		<td><input type='submit' value='<?if($update){echo 'Изменить';}else{echo 'Добавить';}?>'></td>	</tr>		</form>	<?}?></table>