<?if($change){?><form method='post' ><table border=0 cellpadding='5'>	<tr>		<td>Пользователь: </td>	<td>	<select name='id'>		<?foreach($users_list as $user){		?>		<option <?if($id && $id==$user['id'])echo 'selected="selected"';?>value=<?=$user['id']?>><?=$user['login']?></option>		<?		}?>	</select>	<input type='submit' value='Выбрать'>	</td>	</tr></table></form><?}?><?if(!$change || ($change && $id )){?><form method='post' action='/action/users/profile/' enctype="multipart/form-data"><?if($change && $id){?><input type='hidden' name='id' value='<?=$id?>' /><?}?><table border=0 cellpadding='5'>	<tr>		<td>Баланс</td>		<td><?=$balance?> р.</td>	</tr>	<tr>		<td>Новый пароль:</td>		<td><input type='password' name='password' /></td>	</tr>	<tr>		<td>Повтор пароля:</td>		<td><input type='password' name='password2' /></td>	</tr>	<tr>		<td>E-mail:</td>		<td><input type='text' name='mail' value='<?=$mail?>' /></td>	</tr>		<tr>		<td>Аватарка</td>		<td><input type='file' name='img' /></td>	</tr>	<?if($img){?>	<tr>		<td></td>		<td><img src='<?=$img?>'/></td>	</tr>	<?}?>		<?if(isset($activate)){?>	<tr>		<td>Активировать</td>		<td><input type='checkbox' name='activate' value='1' <?if($activate)echo 'checked="checked"'?></td>	</tr>	<tr>		<td>Забанен</td>		<td><input type='checkbox' name='banned' value='1' <?if($banned)echo 'checked="checked"'?></td>	</tr>	<tr>		<td>Причина бана</td>		<td><textarea name='ban_reason' ><?=$ban_reason?></textarea></td>	</tr>			<?}?>	<tr>		<td></td>		<td><?=$captcha->img?></td>	</tr>			<tr>		<td>Введите код</td>		<td><?=$captcha->form?></td>	</tr>	<tr>		<td></td>		<td><input type='submit' value='Изменить'></td>	</tr>		</table></form><?}?>